
function clickStar(d,b){var a="#st"+d;var c="#stv"+d;$(a).attr("class","fa fa-refresh");$.ajax({url:"/ticket/poll",type:"POST",data:{id:d,st:b},success:function(f,e){$(a).removeAttr("data-original-title");if(f==="1"){$(a).attr("class","fa fa-star");$(a).css("color","orange");$(a).attr("onclick","clickStar("+d+",0)");$(c).html(parseInt($(c).text())+1)}else{if(f==="0"){$(a).attr("class","fa fa-star");$(a).css("color","gray");$(a).attr("onclick","clickStar("+d+",1)");$(c).html(parseInt($(c).text())-1)}else{$(a).attr("class","fa fa-warning");$(a).css("color","red");alert("Erreur lors du vote. Le problème est reporté à notre équipe de développement.")}}},error:function(){$(a).attr("class","fa fa-warning");$(a).css("color","red");alert("Le vote n'a pas aboutit. Vérifiez votre connexion internet.")}})}function dispTick(a){$.router.push("/ticket/detail/id/"+a)};